mapper.select:
select * from dispatch_strategy
WHERE
	device_id = #{deviceId}
and batch_size = #{batchSize}
order by end_id desc
limit 1

mapper.update:
update dispatch_strategy


		start_id = #{startId,jdbcType=INTEGER},


		end_id = #{endId,jdbcType=INTEGER},


where device_id = #{deviceId}
and batch_size = #{batchSize}
